mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num, name, caption, img_path, regdate, ended_dt, deadline_dt, start_dt, manager_id, max_people, now_people, avrg_rate, meeting_loc, meeting_time, genre, on_off, manager_comment, viewcount FROM group_tb
ORDER BY num DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num, name, caption, img_path, regdate, ended_dt, deadline_dt, start_dt, manager_id, max_people, now_people, avrg_rate, meeting_loc, meeting_time, genre, on_off, manager_comment, viewcount FROM group_tb
ORDER BY viewcount DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num, name, caption, img_path, regdate, ended_dt, deadline_dt, start_dt, manager_id, max_people, now_people, avrg_rate, meeting_loc, meeting_time, genre, on_off, manager_comment, viewcount FROM group_tb WHERE genre = #{genre}
ORDER BY num DESC) result1)
WHERE rnum >= #{dto.startRowNum} AND rnum <= #{dto.endRowNum}

mapper.update:
UPDATE group_tb
SET title = #{title}, caption=#{caption}, img_path=#{img_path}
WHERE num = #{num}

UPDATE group_tb
SET viewcount=viewcount+1
WHERE num=#{num}
