mapper.select:
SELECT id, pwd, profile, email, regdate, Name, SVC_USE_PCY_AGMT_YN, PS_INFO_PROC_AGMT_YN, MKT_INFO_RECV_AGMT_YN
FROM users
WHERE id = #{id}

mapper.update:
UPDATE users
SET pwd = #{newPwd}
WHERE id = #{id}

UPDATE users
SET email = #{email}, profile= #{profile}
WHERE id = #{id}

mapper.select:
SELECT id, email
FROM users
WHERE name = #{name}
